package school.attractor.payment_module.domain.order;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import school.attractor.payment_module.domain.transaction.Transaction;import school.attractor.payment_module.domain.transaction.TransactionStatus;import javax.persistence.*;import java.util.Date;import java.util.List;@Entity@Data@AllArgsConstructor@NoArgsConstructor@Builder@Table(name = "orders")public class Order {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer id;    @Column    private String shopName;    @Column    private String shopId;    @Column    private String userName;    @Column    private String email;    @Column    private Date date;    @Column    private int amount;    @Column    private TransactionStatus status;    @OneToMany    List<Transaction> transactions;}