package school.attractor.payment_module.domain.order;import lombok.AllArgsConstructor;import org.springframework.data.crossstore.ChangeSetPersister;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.stereotype.Service;import org.springframework.web.client.HttpClientErrorException;import school.attractor.payment_module.domain.exception.OrderNotFound;import school.attractor.payment_module.domain.transaction.Transaction;import java.util.Optional;@Service@AllArgsConstructorpublic class OrderService {    public OrderRepository orderRepository;    public Page<Order> getOrders(Pageable pageable) {        return orderRepository.findAll(pageable);    }    public OrderDTO findByOrderId(int id) {        Optional<Order> order = orderRepository.findById(id);        return OrderDTO.from(order.orElseThrow( OrderNotFound::new));    }}