package school.attractor.payment_module.domain.order;import lombok.AllArgsConstructor;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.stereotype.Service;import school.attractor.payment_module.domain.transaction.Transaction;@Service@AllArgsConstructorpublic class OrderService {    public OrderRepository orderRepository;    public Page<Order> getOrders(Pageable pageable) {        return orderRepository.findAll(pageable);    }}